<script>
export default {
  data () {
    return {
      offset: null
    }
  },
  methods: {
    scrol () {
      this.offset = window.pageYOffset
      console.log(this.offset)
      var k = (this.offset * 25 / 3000) + 1
      var p = (this.offset * 2.5 / 3000) + 1
      var l = (this.offset * 2.5 / 3000) + 1
      var c = document.getElementById('fog')
      var b = document.getElementById('mantain_3')
      b.style.backgroundPositionX = this.offset * -0.15 + 'px'
      b.style.transform = 'scale(' + p + ')'
      document.getElementById('mantain_2').style.backgroundPositionX = this.offset * 0.15 + 840 + 'px'
      document.getElementById('mantain_2').style.transform = 'scale(' + l + ')'
      c.style.transform = 'scale(' + k + ')'
      if (k > 5) {
        c.style.opacity = 0
      }
      if (k < 5) {
        c.style.opacity = 1
      }
    }
  },
  created () {
    window.addEventListener('scroll', this.scrol)
  }
}
</script>

